import { View, StyleSheet, Text, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { Ionicons } from '@expo/vector-icons';
import * as React from 'react';
import * as WebBrowser from "expo-web-browser";
import * as Google from 'expo-auth-session/providers/google';
import AsyncStorage from '@react-native-async-storage/async-storage';



const SecondPage = ({ navigation }) => {
    const [userInfo, setUserInfo] = React.useState(null);
    const [request, response, promptAsync] = Google.useAuthRequest({
        androidClientId: "",
        iosClientId: "",
        webClientId: "",
    });

    const pressMail = () => {
        navigation.goBack(); // Fermer la modal
        navigation.navigate('LoginScreen', { provider: 'mail' }); // Rediriger vers la page souhait√©e avec le fournisseur 'mail'
    };

    return (
        <View style={styles.container}>
            <View style={styles.line} />
            <TouchableOpacity style={styles.googlebutton} onPress={promptAsync}>
                <View style={styles.container1}>
                    <Ionicons name="logo-google" size={24} color='white'style={styles.icon} />
                    <Text style={styles.googletext}> Continue with google</Text>
                </View>
            </TouchableOpacity>
            <TouchableOpacity style={styles.facebookbutton}>
                <View style={styles.container1}>
                    <Ionicons name="logo-facebook" size={24} color="white" style={styles.icon} />
                    <Text style={styles.facebooktext}>Continue with Facebook</Text>
                </View>
            </TouchableOpacity>

            <TouchableOpacity style={styles.mailbutton} onPress={pressMail}>
                <View style={styles.container1}>
                    <Ionicons name="mail" size={24} color="white" style={styles.icon} />
                    <Text style={styles.mailtext}>Continue with Mail</Text>
                </View>
            </TouchableOpacity>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        alignItems: 'center',
    },
    container1: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        // alignItems: 'center',
    },
    line: {
        marginTop: 40,
        width: 100,
        height: 10,
        borderRadius: 10,
        backgroundColor: 'black',
        borderWidth: 1,
        borderColor: 'black',
    },
    googletext: {
        color: 'white',
        fontSize: 15,
        marginLeft: 30,
    },
    facebooktext: {
        color: 'white',
        fontSize: 15,
        marginLeft: 30,
    },
    mailtext: {
        color: 'white',
        fontSize: 15,
        marginLeft: 30,
    },
    googlebutton: {
        marginTop: 20,
        width: 300,
        backgroundColor: 'black',
        borderRadius: 20,
        paddingHorizontal: 10,
        paddingVertical: 10,
    },
    facebookbutton: {
        width: 300,
        marginTop: 20,
        backgroundColor: 'black',
        borderRadius: 20,
        paddingHorizontal: 10,
        paddingVertical: 10,
    },
    mailbutton: {
        width: 300,
        marginTop: 20,
        backgroundColor: 'black',
        borderRadius: 20,
        paddingHorizontal: 10,
        paddingVertical: 10,
    },
    icon: {
        marginRight: 50,
    }
});

export default SecondPage;
